function format(title){
  return title.trim().replace(/^(\d+)(?![\s\d卷部章节回.])/, '$1 ').replace(/\s+/g, "\x20\x20");
}

jdoc.forEach((vol,idx) => {
  var chs = vol.chapters.map(ch => ({
    text: (opts.showVolName ? '🍈[' + format(vol.volume_name) + ']🍈' : (ch.IsVip === '1' ? '💰' : '').padStart(3, "\u2000")) + format(ch.chapter_name),
    href: chUrl+vol.volume_id+'_'+ch.chapter_id+'.txt',
    name: format(ch.chapter_name)
  }));

  var volInfo =author+"：\n"+sm+'\\'+ format(vol.volume_name)+ '\n目录(' + chs.length + ")：\n";
  volInfo += chs.slice(0,30).map(v => v.name).join("\n");
  volInfo += chs.length > 30 ? "\n……" : '';

  list.push({
    text: '♦[' + format(vol.volume_name) + ']♦',
    href: volUrl + encodeURIComponent(volInfo)
});
  list = list.concat(chs);
});

result = list;
</js>

